CREATE TABLE `doctors` (
  `id` int(11) UNSIGNED NOT NULL,
  `order_no` int(11) DEFAULT NULL,
  `name` varchar(350) NOT NULL DEFAULT '',
  `nickname` varchar(80) DEFAULT NULL,
  `license` int(11) DEFAULT NULL,
